TITLE: Implementing Complete OAuth Server Provider in TypeScript
DESCRIPTION: A mock OAuth server provider implementation with full functionality including client store, authorization, token exchange, verification, and revocation. This provider supports all standard OAuth endpoints and operations.
SOURCE: https://github.com/eyaltoledano/claude-task-master/blob/main/context/mcp-js-sdk-docs.txt#2025-04-23_snippet_98

LANGUAGE: TypeScript
CODE:
```
const mockProvider: OAuthServerProvider = {
  clientsStore: mockClientStore,

  async authorize(client: OAuthClientInformationFull, params: AuthorizationParams, res: Response): Promise<void> {
    const redirectUrl = new URL(params.redirectUri);
    redirectUrl.searchParams.set('code', 'mock_auth_code');
    if (params.state) {
      redirectUrl.searchParams.set('state', params.state);
    }
    res.redirect(302, redirectUrl.toString());
  },

  async challengeForAuthorizationCode(): Promise<string> {
    return 'mock_challenge';
  },

  async exchangeAuthorizationCode(): Promise<OAuthTokens> {
    return {
      access_token: 'mock_access_token',
      token_type: 'bearer',
      expires_in: 3600,
      refresh_token: 'mock_refresh_token'
    };
  },

  async exchangeRefreshToken(): Promise<OAuthTokens> {
    return {
      access_token: 'new_mock_access_token',
      token_type: 'bearer',
      expires_in: 3600,
      refresh_token: 'new_mock_refresh_token'
    };
  },

  async verifyAccessToken(token: string): Promise<AuthInfo> {
    if (token === 'valid_token') {
      return {
        token,
        clientId: 'valid-client',
        scopes: ['read', 'write'],
        expiresAt: Date.now() / 1000 + 3600
      };
    }
    throw new InvalidTokenError('Token is invalid or expired');
  },

  async revokeToken(_client: OAuthClientInformationFull, _request: OAuthTokenRevocationRequest): Promise<void> {
    // Success - do nothing in mock
  }
