function updateTaskById(taskId, options) {
  // Find specific task by ID
  const task = tasks.find(t => t.id === taskId);

  if (!task) {
    throw new TaskNotFoundError(`Task with ID ${taskId} not found`);
  }

  // Preserve completion status
  const completedSubtasks = task.subtasks.filter(st => st.completed);

  // Update task using AI refinement
  const updatedTask = await refineTaskWithAI(task, options);

  // Restore completion status
  updatedTask.subtasks = mergeCompletedSubtasks(updatedTask.subtasks, completedSubtasks);

  return updatedTask;
}
