LANGUAGE: java
CODE:
```
public class EngineSoundInstance extends AbstractDynamicSoundInstance {
    private final EngineBlockEntity engine;

    public EngineSoundInstance(SoundEvent sound, EngineBlockEntity engine, SoundInstanceCallback callback) {
        super(sound, SoundCategory.BLOCKS, engine, callback);
        this.engine = engine;
    }

    @Override
    protected boolean shouldContinuePlaying() {
        return super.shouldContinuePlaying() && engine.isRunning();
    }

    @Override
    protected void updateSound() {
        super.updateSound();
        volume = lerpValue(minVolume, maxVolume, getTransitionVolume());
        pitch = lerpValue(minPitch, maxPitch, engine.getStress());
    }
}
