    public Type<UseToolCriterion> getType() {
        return TYPE;
    }

    @Override
    protected Conditions conditionsFromJson(JsonObject obj, LootContext.EntityTarget playerTarget, DeserializationContext predicateDeserializer) {
        return new Conditions(EntityPredicate.contextPredicateFromJson(obj, "player", predicateDeserializer));
    }

    // Will be implemented below
    public void trigger(ServerPlayerEntity player) {
        // ...
    }

    public static class Conditions extends AbstractCriterionConditions {
        private static final ContextAwarePredicate DEFAULT_PREDICATE = ContextAwarePredicate.createEmpty();

        // Codec for the Conditions class (you can ignore this for now)
        public static final Codec<Conditions> CODEC = LootContext.EntityTarget.field_44603
                .get("player")
                .flatXmap(
                        type -> Codec.unit(DEFAULT_PREDICATE),
                        predicate -> DataResult.success(LootContext.EntityTarget.PLAYER)
                )
                .xmap(Conditions::new, Conditions::getPlayerPredicate);

        public Conditions(ContextAwarePredicate playerPredicate) {
            super(playerPredicate);
        }

        public ContextAwarePredicate getPlayerPredicate() {
            return this.playerPredicate;
        }
    }
