protected void updateSound() {
    switch (state) {
        case INTRO:
            if (ticksActive >= introDuration) {
                ticksActive = 0;
                state = TransitionState.LOOP;
            }
            break;
        case LOOP:
            break;
        case OUTRO:
            if (ticksActive >= outroDuration) {
                stopPlaying();
            }
            break;
    }
}
