    public TypedActionResult<ItemStack> use(World world, PlayerEntity user, Hand hand) {
        if (!world.isClient) {
            BlockPos pos = user.getBlockPos();
            Vec3d rotation = user.getRotationVector();
            Vec3d pos3d = user.getPos();

            double x = pos3d.x + rotation.x * 10;
            double z = pos3d.z + rotation.z * 10;

            BlockPos targetPos = new BlockPos((int) x, pos.getY(), (int) z);
            EntityType.LIGHTNING_BOLT.spawn(((ServerWorld) world), targetPos, SpawnReason.TRIGGERED);
        }

        return TypedActionResult.success(user.getStackInHand(hand));
    }
}
