@Override
public void renderButton(DrawContext context, int mouseX, int mouseY, float delta) {
    // Choose a fill color based on hover state
    int fillColor = this.isHovered() ? 0xFF444444 : 0xFF000000;

    // Render the background of the widget
    context.fill(getX(), getY(), getX() + width, getY() + height, fillColor);

    // Render the border of the widget
    context.drawBorder(getX(), getY(), width, height, 0xFFFFFFFF);

    // Get the text renderer
    TextRenderer textRenderer = MinecraftClient.getInstance().textRenderer;

    // Render the message of the widget
    int textWidth = textRenderer.getWidth(getMessage());
    context.drawTextWithShadow(textRenderer, getMessage(), getX() + (width - textWidth) / 2, getY() + (height - 8) / 2, 0xFFFFFFFF);
}
