    private FabricDocsReferenceBlocks() {
    }

    // === BLOCKS ===
    public static final Block CONDENSED_DIRT = register("condensed_dirt", new Block(AbstractBlock.Settings.create()
            .mapColor(MapColor.DIRT)
            .strength(0.5f)
            .sounds(BlockSoundGroup.GRAVEL)), true);

    // Register a block
    private static <T extends Block> T register(String id, T block, boolean shouldRegisterItem) {
        // Register the block
        Identifier blockId = new Identifier(FabricDocsReference.MOD_ID, id);
        Registry.register(Registries.BLOCK, blockId, block);

        // Register the block item, if requested
        if (shouldRegisterItem) {
            Registry.register(Registries.ITEM, blockId, new BlockItem(block, new Item.Settings()));
        }

        return block;
    }

    public static void initialize() {
        // This method is never actually called
        // We just want the class to be loaded and have its static initializers run

        FabricDocsReference.LOGGER.info("Initializing blocks");
    }
}
