public class CustomSoundInstance extends MovingSoundInstance {

    private final LivingEntity entity;

    public CustomSoundInstance(SoundEvent sound, SoundCategory category, LivingEntity entity) {
        super(sound, category, entity.world.random);
        this.entity = entity;
        this.repeat = true; // Make it repeat indefinitely
        this.repeatDelay = 0; // No delay between repetitions
        this.volume = 1.0F; // Initial volume, can be changed later
    }

    @Override
    public void tick() {
        // Stop the sound if the entity no longer exists or is removed
        if (!this.entity.isAlive()) {
            this.setDone();
            return;
        }

        // Update the position to follow the entity
        this.x = this.entity.getX();
        this.y = this.entity.getY();
        this.z = this.entity.getZ();

        // Optionally update other properties based on entity state
        // e.g., this.pitch = this.entity.isSprinting() ? 1.5F : 1.0F;
    }
}
