public class FabricDocsReferenceEffects implements ModInitializer {
    // We declare our custom effect identifier here
    public static final Identifier TATER = new Identifier("fabric-docs-reference", "tater");

    // We also create and store our custom effect entry
    public static final RegistryEntry<StatusEffect> TATER_EFFECT;

    @Override
    public void onInitialize() {
        // Registration happens in the static initialization block below
    }

    // This static block ensures that our effects are registered when the class is loaded
    static {
        // Use Registry.register to add our custom effect to the STATUS_EFFECT registry
        TATER_EFFECT = Registry.register(Registries.STATUS_EFFECT, TATER, new TaterEffect());
    }
}
