protected float getTransitionVolume() {
    float normalizedTime = (float) ticksActive / (state == TransitionState.INTRO ? introDuration : outroDuration);
    return state == TransitionState.INTRO ? normalizedTime : 1 - normalizedTime;
}

protected float lerpValue(float min, float max, float t) {
    return min + (max - min) * t;
