const asset = useFragment(
  graphql`
    fragment MyFragment on Asset {
      id
      name @required(action: LOG)
      slug @required(action: LOG)
      color
      supply {
        total @required(action: LOG)
        circulating @required(action: LOG)
      }
    }
  `,
  assetRef,
);

