  <>
    <Title color={asset.color}>{asset.name}</Title>
    <Subtitle>{asset.slug}</Subtitle>
    {asset.supply && (
      <SupplyStats total={asset.supply.total} circulating={asset.supply.circulating} />
    )}
  </>
);
