import InfiniteScrollTrigger from "./InfiniteScrollTrigger";

function Newsfeed() {
  const queryData = useLazyLoadQuery<NewsfeedQueryType>(
    NewsfeedQuery,
    {},
  );
  const {
    data,
    loadNext,
    // change-line
    hasNext,
    // change-line
    isLoadingNext,
  } = usePaginationFragment(NewsfeedContentsFragment, queryData);
  // change
  function onEndReached() {
