class LikeStoryMutation extends Relay.Mutation {
  getMutation() {
    return Relay.QL`mutation {likeStory}`;
  }
  getVariables() {
    return {storyID: this.props.story.id};
  }
  getFatQuery() {
    return Relay.QL`
      fragment on LikeStoryPayload {
        story {
          likers {
            count,
          },
          likeSentence,
          viewerDoesLike,
        },
      }
    `;
