/**
 * @RelayResolver Client3DFoo implements IClient3D
 */
function Client3DFoo(id: DataID): ?Client3DModel {
  if (id === INVALID_ID) {
    return null;
  }
  return {
    __id: id,
  };
}

/**
 * @RelayResolver Client3DFoo.data: Client3DData
 */
function data(client3DModel: Client3DModel): Client3DData {
  return {
    type: 'FOO',
    info: 'someFooInfo',
  }
}
```

