TITLE: Implementing Interactive Chat Loop
DESCRIPTION: Implementation of an interactive CLI chat interface with conversation history management.
SOURCE: https://github.com/fallomai/spinai/blob/main/apps/docs/mcp/example.mdx#2025-04-23_snippet_5

LANGUAGE: typescript
CODE:
```
import { createAgent, createActionsFromMcpConfig } from "spinai";
import * as dotenv from "dotenv";
import { openai } from "@ai-sdk/openai";
import * as readline from "readline";
// @ts-ignore
import mcpConfig from "../mcp-config.ts";

dotenv.config();

const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout,
});

async function chat() {
  console.log("Setting up...");
  const mcpActions = await createActionsFromMcpConfig(mcpConfig);
  console.log("\nðŸ¤– Ready! Type your message (or 'exit' to quit)\n");

  const agent = createAgent({
    instructions: `You are a GitHub assistant that can help with repository management.
    Use the available GitHub actions to help users with their requests.`,
    actions: [...mcpActions],
    model: openai("gpt-4o-mini"),
  });

  let messageHistory: any[] = [];

  function getInput() {
    rl.question("> ", async (input) => {
      if (input.toLowerCase() === "exit") {
        rl.close();
        return;
      }

      try {
        const { messages, response } = await agent({
          input,
          messages: messageHistory,
        });

        // Update message history
        messageHistory = messages;

        // Show any tool calls
        const toolCalls = messages.filter((m) => m.role === "tool");
        if (toolCalls.length > 0) {
          console.log("\nActions:");
          toolCalls.forEach((call) => {
            console.log(`â€¢ ${call.content[0].toolName}`);
          });
          console.log();
        }

        console.log(`Assistant: ${response}\n`);
      } catch (error) {
        console.error("\nError:", error, "\n");
      }

      getInput();
    });
  }

  getInput();
}

chat().catch(console.error);

// Handle clean exit
rl.on("close", () => {
  console.log("\nGoodbye!");
  process.exit(0);
});
```

