    })
  },
  validatorCompiler: ({ schema, method, url, httpPart }) => {
    return function (data) {
      try {
        const result = schema.validateSync(data, yupOptions)
        return { value: result }
      } catch (e) {
        return { error: e }
      }
    }
  }
}, handler)
```

