async ({ query }) => {
const documents = FAKE_DOCUMENT_CONTENT.map((text) =>
Document.fromText(text)
);
const reranker = 'vertexai/reranker';

    const rerankedDocuments = await rerank({
      reranker,
      query: Document.fromText(query),
      documents,
