TITLE: Implementing a Lottery using Secure Random Numbers in Solidity
DESCRIPTION: Solidity contract implementing a basic lottery where participants can enter, and a winner is chosen using a secure random number obtained from Flare's `RandomNumberV2` contract. Requires setting the EVM version to `london`.
SOURCE: https://github.com/flare-foundation/developer-hub.git/blob/main/docs/network/guides/secure-random-numbers.mdx#2025-04-22_snippet_3

LANGUAGE: solidity
CODE:
```
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

// Interface to obtain secure random numbers on Flare.
interface IRandomNumberV2 {
    function getRandomNumberV2() external view returns (uint256 randomNumber, bool isSecure, uint256 timestamp);
}

contract RandomNumberV2Lottery is Ownable {
    // Flare mainnet, Coston2 and Songbird address
    // https://dev.flare.network/ftso/address-list
    IRandomNumberV2 constant randomNumberV2 = IRandomNumberV2(0xE0485523683136F6d42a222B9b25d170FFB0F252);

    address[] public players;
    address public winner;
    uint256 public lotteryId;
    mapping(uint256 => address) public lotteryHistory;

    event LotteryWinner(uint256 lotteryId, address winner);

    constructor() Ownable(msg.sender) {}

    function getPlayers() public view returns (address[] memory) {
        return players;
    }

    function enter() public {
        // Basic requirement to avoid duplicate entries
        // Real implementation might require payment, checks, etc.
        bool alreadyEntered = false;
        for (uint i = 0; i < players.length; i++) {
            if (players[i] == msg.sender) {
                alreadyEntered = true;
                break;
            }
        }
        require(!alreadyEntered, "Player already entered");
        players.push(msg.sender);
    }

    function pickWinner() public onlyOwner {
        require(players.length > 0, "No players in the lottery");

        // Get the secure random number
        (uint256 randomNumber, bool isSecure, ) = randomNumberV2.getRandomNumberV2();

        // Ensure the random number is secure before using it
        require(isSecure, "Random number is not secure");

        // Use the random number to pick a winner
        uint256 index = randomNumber % players.length;
        winner = players[index];

        // Record the winner for this lottery round
        lotteryHistory[lotteryId] = winner;

        // Emit event
        emit LotteryWinner(lotteryId, winner);

        // Reset the lottery for the next round
        players = new address[](0);
        lotteryId++;
    }

    // Function to allow withdrawal of funds if Ether were involved (example placeholder)
    function withdraw() public onlyOwner {
        // Implementation for withdrawing potential funds
    }
}

```

