import * as dotenv from "dotenv";
import hre, { ethers } from "hardhat";

dotenv.config();

async function main() {
  // Get the address of the weather insurance agency contract
  const agencyAddress = process.env.WEATHER_INSURANCE_AGENCY_ADDRESS ?? "";
  // Get the address of the USDC stablecoin contract
  const stablecoinAddress = process.env.STABLECOIN_ADDRESS ?? "";

  const [deployer] = await ethers.getSigners();
  console.log("Creating policy with the account:", deployer.address);

  // Set policy parameters
  const latitude = 46.056946; // Ljubljana
  const longitude = 14.505751;
  const threshold = -10.0; // -10.0°C
  const period = 86400 * 7; // 7 days
  // const startTimeMs = Date.now() + 60 * 1000; // starts in 1 minute
  const startTimeMs = Date.now() + 24 * 60 * 60 * 1000; // starts in 1 day
  const startTime = Math.floor(startTimeMs / 1000);
  const expiryTime = startTime + period;
  const premium = ethers.parseEther("0.2"); // 0.2 USDC
  const coverage = ethers.parseEther("5"); // 5 USDC
  console.log("latitude:", latitude);
  console.log("longitude:", longitude);
  console.log("threshold:", threshold);
  console.log("period:", period);
  console.log("startTime:", startTime);
  console.log("expiryTime:", expiryTime);
  console.log("premium:", ethers.formatEther(premium));
  console.log("coverage:", ethers.formatEther(coverage));

  const IStablecoin = new ethers.Interface([
    "function name() view returns (string)",
    "function symbol() view returns (string)",
    "function decimals() view returns (uint8)",
    "function totalSupply() view returns (uint256)",
    "function balanceOf(address) view returns (uint256)",
    "function transfer(address to, uint256 amount) returns (bool)",
    "function allowance(address owner, address spender) view returns (uint256)",
    "function approve(address spender, uint256 amount) returns (bool)",
    "function transferFrom(address from, address to, uint256 amount) returns (bool)",
  ]);
  const stablecoin = new ethers.Contract(stablecoinAddress, IStablecoin, deployer);
  console.log("stablecoin:", await stablecoin.name(), await stablecoin.symbol());

  const WeatherInsuranceAgency = await hre.ethers.getContractFactory("WeatherInsuranceAgency");
  const weatherInsuranceAgency = WeatherInsuranceAgency.attach(agencyAddress);

  console.log(
    "Approving stablecoin transfer to WeatherInsuranceAgency:",
    await weatherInsuranceAgency.getAddress()
  );
  const approveTx = await stablecoin.approve(await weatherInsuranceAgency.getAddress(), premium);
  await approveTx.wait();

  console.log("Creating policy");
  const tx = await weatherInsuranceAgency.createPolicy(
    latitude * 1000000,
    longitude * 1000000,
    threshold * 10,
    startTime,
    expiryTime,
    premium,
    coverage,
    stablecoinAddress
  );
  await tx.wait();
  console.log("Policy created. Tx hash:", tx.hash);
}

main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});

