import "@flarenetwork/flare-periphery-contracts/contracts/fassets/interfaces/IAssetManager.sol";

/// @title FAssetsSettings: Fetch FAsset Lot Size and Value
/// @notice This contract fethes the lot size of an FAsset and return both the lot size and the asset decimals
contract FAssetsSettings {
    /// The AssetManager interface for FXRP from the flare-periphery-contracts package
    IAssetManager public assetManager;

    /// @notice The constructor initializes the contract with the AssetManager address
    /// @param _assetManagerAddress The address of the FXRP Asset Manager contract
    constructor(address _assetManagerAddress) {
        assetManager = IAssetManager(_assetManagerAddress);
    }

    /// @notice Fetches the smallest transferable amount (lot size) and asset decimals
    /// @return lotSizeAMG The minimum transferable amount in Asset Minimum Unit (AMG)
    /// @return assetDecimals The number of decimal places in the asset
    function getLotSize() external view returns (uint256 lotSizeAMG, uint8 assetDecimals) {
        // Get settings which include the lot size
        // Additional settings are available in the IAssetManager.AssetManagerSettings struct
        IAssetManager.AssetManagerSettings memory settings = assetManager.getSettings();

        // Return the lot size (minUnderlyingBackingOutput) and decimals
        return (settings.minUnderlyingBackingOutput, settings.assetDecimals);
    }
}
