TITLE: Implementing Speech Recognition Custom Form Field in Vue.js
DESCRIPTION: A complete Vue.js Single File Component that implements a speech recognition form field using the Web Speech API. It accepts a sentence prop, listens to the user's speech, and validates if all words were correctly pronounced. The component integrates with @formwerk/core for form handling.
SOURCE: https://github.com/formwerkjs/formwerk.dev/blob/main/src/content/docs/guides/fields/_partials/_speechCustomField.mdx#2025-04-13_snippet_0

LANGUAGE: vue
CODE:
```
<script setup lang="ts">
import { ref, computed } from 'vue';
import { useCustomField, type CustomFieldProps } from '@formwerk/core';

const props = defineProps<
  CustomFieldProps & {
    sentence: string;
  }
>();

const {
  labelProps,
  controlProps,
  setValue,
  errorMessage,
  errorMessageProps,
  setTouched,
  isTouched,
} = useCustomField<boolean>(props);

const words = computed(() => props.sentence.split(' '));
const results = ref<string[]>([]);
const isListening = ref(false);
const recognition = new (window.SpeechRecognition ||
  window.webkitSpeechRecognition)();

recognition.lang = 'en-US';
recognition.interimResults = false;
recognition.maxAlternatives = 1;

function onSpeakClick() {
  if (isListening.value) {
    stopListening();
    return;
  }

  results.value = [];
  isListening.value = true;
  recognition.start();
  setTimeout(() => stopListening(true), 8000);
}

function isAllSpoken() {
  return results.value.every((word) => words.value.includes(word));
}

function stopListening(done = false) {
  recognition.stop();
  isListening.value = false;

  if (done) {
    setTouched(true);
    setValue(isAllSpoken());
  }
}

recognition.onresult = (event) => {
  results.value = event.results[0][0].transcript.toLowerCase().split(' ');
  setValue(isAllSpoken());
};
</script>

<template>
  <div class="speech-exercise">
    <div class="challenge-container">
      <div class="challenge-label" v-bind="labelProps">{{ label }}</div>
      <div class="challenge">
        <span
          v-for="(word, idx) in words"
          :key="`word-${idx}`"
          class="challenge-word"
          :class="{ 'is-spoken': results.includes(word.toLowerCase()) }"
        >
          {{ word }}
        </span>
      </div>
    </div>

    <button
      type="button"
      v-bind="controlProps"
      @click="onSpeakClick"
      :class="{ 'is-listening': isListening }"
    >
      <div class="button-content">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="24"
          height="24"
          viewBox="0 0 24 24"
          fill="none"
          stroke="currentColor"
          stroke-width="2"
          stroke-linecap="round"
          stroke-linejoin="round"
        >
          <path d="M12 1a3 3 0 0 0-3 3v8a3 3 0 0 0 6 0V4a3 3 0 0 0-3-3z" />
          <path d="M19 10v2a7 7 0 0 1-14 0v-2" />
          <line x1="12" y1="19" x2="12" y2="23" />
          <line x1="8" y1="23" x2="16" y2="23" />
        </svg>
        {{ isListening ? 'Listening...' : 'Speak' }}
      </div>
    </button>

    <div
      v-if="errorMessage && isTouched"
      v-bind="errorMessageProps"
      class="error"
    >
      {{ errorMessage }}
    </div>
  </div>
</template>

<style scoped>
.speech-exercise {
  background: white;
  border-radius: 16px;
}

.challenge-container {
  margin-bottom: 2rem;
}

.challenge {
  font-size: 1.2rem;
  font-weight: 700;
  color: #57534e;
  margin-bottom: 1rem;
  display: flex;
  flex-wrap: wrap;
  gap: 0.25rem;
}

.challenge-label {
  font-size: 14px;
  color: #9ca3af;
  margin-bottom: 0.5rem;
}

.challenge-word {
  &.is-spoken {
    color: #059669;
  }
}

.result {
  font-size: 1.1rem;
  color: #4b5563;
}

.result-label {
  color: #6b7280;
  font-size: 0.9rem;
  margin-right: 0.5rem;
}

button {
  width: 100%;
  padding: 1rem;
  border: none;
  border-radius: 8px;
  background-color: #059669;
  color: white;
  font-size: 1.1rem;
  font-weight: 700;
  cursor: pointer;
  transition: all 0.2s ease;
}

button:hover {
  background-color: #10b981;
  transform: translateY(-1px);
}

button:active {
  transform: translateY(1px);
}

.button-content {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
}

.is-listening {
  background-color: #ef4444;
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0% {
    box-shadow: 0 0 0 0 rgba(239, 68, 68, 0.4);
  }
  70% {
    box-shadow: 0 0 0 10px rgba(239, 68, 68, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(239, 68, 68, 0);
  }
}

.error {
  color: #ef4444;
  font-size: 14px;
  margin-top: 0.5rem;
}
</style>
```

