TITLE: Creating a Slider Component in Vue with Formwerk
DESCRIPTION: This code snippet shows how to build a slider component using the useSlider composable from Formwerk. It includes the necessary template structure, bindings, and styling for a functional slider with a single thumb.
SOURCE: https://github.com/formwerkjs/formwerk.dev/blob/main/src/content/docs/guides/fields/sliders.mdx#2025-04-13_snippet_1

LANGUAGE: vue
CODE:
```
<script setup lang="ts">
import { useSlider, useThumbMetadata } from '@formwerkjs/formwerk';
import Thumb from './Thumb.vue';

const props = withDefaults(
  defineProps<{
    modelValue?: number;
    label?: string;
    disabled?: boolean;
    readonly?: boolean;
    min?: number;
    max?: number;
    step?: number;
    dir?: 'ltr' | 'rtl';
    orientation?: 'horizontal' | 'vertical';
  }>(),
  {
    min: 0,
    max: 100,
    step: 1,
    dir: 'ltr',
    orientation: 'horizontal',
  }
);

const emit = defineEmits<{
  (e: 'update:modelValue', value: number): void;
}>();

const {
  sliderRef,
  sliderProps,
  trackRef,
  trackProps,
  thumbsMetadata,
  labelProps,
} = useSlider(props, emit);

const { getThumbPercentage } = useThumbMetadata(thumbsMetadata);
</script>

<template>
  <div class="slider-wrapper">
    <label v-bind="labelProps">{{ label }}</label>
    <div
      ref="sliderRef"
      v-bind="sliderProps"
      class="slider"
      :class="{
        'slider--disabled': disabled,
        'slider--readonly': readonly,
      }"
    >
      <div ref="trackRef" v-bind="trackProps" class="slider__track">
        <div
          class="slider__progress"
          :style="{ width: `${getThumbPercentage(0)}%` }"
        />
      </div>
      <Thumb :value="modelValue" :disabled="disabled" />
    </div>
  </div>
</template>

<style scoped>
.slider-wrapper {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  width: 100%;
}

.slider {
  position: relative;
  height: 20px;
