      {{ errorMessage }}
    </div>
  </div>
</template>

<style scoped>
.slider {
  --track-width: 150px;

  .track {
    display: flex;
    align-items: center;
    width: var(--track-width);
    margin-top: 24px;
    margin-bottom: 24px;
    height: 6px;
    background-color: #a1a1aa;
    border-radius: 6px;

    &::before {
      content: '';
      width: calc(
        (v-bind('t2.sliderPercent') - v-bind('t1.sliderPercent')) * 100%
      );
      background-color: #10b981;
      border-radius: 6px;
      height: 6px;
      translate: calc(v-bind('t1.sliderPercent') * var(--track-width)) 0;
    }
  }
}
</style>
