  --color-focus: var(--color-primary);
  --color-white: #fff;
  --color-gray-100: #fafafa;
  --color-gray-400: #a1a1aa;
  --color-error: #f00;

  .checkbox-square {
    width: 1rem;
    height: 1rem;
    border-radius: 0.375rem;
    flex-shrink: 0;
    border: 1px solid var(--color-gray-400);
    display: flex;
    align-items: center;
    justify-content: center;
