LANGUAGE: sway
CODE:
```
library;

pub const SOME_CONSTANT = 42;

pub struct Point {
    x: u64,
    y: u64,
}

pub enum Color {
    Red: (),
    Blue: (),
    Green: (),
}

pub trait Shape {
    fn area(self) -> u64;
}

pub fn add_one(x: u64) -> u64 {
    x + 1
}
