// Convert from u32 to byte array
let c: [u8; 4] = u32_to_byte_array(5u32);

// Convert from u64 to byte array
let d: [u8; 8] = u64_to_byte_array(5u64);

// Convert from u256 to byte array
let e: [u8; 32] = u256_to_byte_array(u256 { bits: [0, 0, 0, 5] });

// Convert from b256 to byte array
