contract;

storage {
    value: u64 = 0,
}

abi Target {
    #[storage(read, write)]
    fn double_input(input: u64) -> u64;
}

impl Target for Contract {
    #[storage(read, write)]
    fn double_input(input: u64) -> u64 {
        let new_value = input * 2;
        storage.value = new_value;
        new_value
    }
}
```

