import { useAccount } from "jazz-react";

function ProjectList() {
  const { me } = useAccount({
    resolve: {
      profile: true,
      root: {
        myProjects: {
          $each: {
            tasks: true
          }
        }
      },
    },
  });

  if (!me) {
    return <div>Loading...</div>;
  }

  return <div>
    <h1>{me.profile.name}'s projects</h1>
    <ul>
      {me.root.myProjects.map(project => (
        <li key={project.id}>
          {project.name} ({project.tasks.length} tasks)
        </li>
      ))}
    </ul>
  </div>
}
```

