```
class ListOfTracks extends CoList.Of(co.optional.ref(Track)) {}

function TrackListComponent({ id }: { id: ID<ListOfTracks> }) {
  // Before (ambiguous states)
  // @ts-expect-error
  const tracks = useCoState(ListOfTracks, id, [{}]);
  if (tracks === undefined) return <div>Loading or access denied</div>;
  if (tracks === null) return <div>Not found</div>;

  // After
  const tracks = useCoState(ListOfTracks, id, { resolve: { $each: true } });
  if (tracks === undefined) return <div>Loading...</div>;
  if (tracks === null) return <div>Not found or access denied</div>;

