        project?.issues.push(Issue.create({ 
            title: "",
            description: "",
            estimate: 0,
            status: "backlog",
        }, project._owner));
    };

    return project ? (
        <div>
            <h1>{project.name}</h1>
            {me.canAdmin(project) && ( // [!code ++:6]
                <>
                    <button onClick={() => invite("reader")}>Invite Guest</button>
                    <button onClick={() => invite("writer")}>Invite Member</button>
                </>
            )}
            <div className="border-r border-b">
