task.status = "in-progress";
task.priority = "high";

// See all edits for a field
for (const edit of task._edits.status.all) {
  console.log(
    `${edit.madeAt.toISOString()}: Status changed to "${edit.value}" by ${edit.by?.id}`,
  );
}

// Get the initial value
