const SearchBox = ({ className, onFocus, onChange }) => {
  const { query, refine } = useSearchBox()

  return (
    <form className={className}>
      <input
        className="SearchInput"
        type="text"
        placeholder="Search"
        aria-label="Search"
        onChange={e => {
