import type { GatsbyNode } from "gatsby"

export const createSchemaCustomization: GatsbyNode[`createSchemaCustomization`] =
  ({ actions, schema }) => {
    const { createTypes } = actions

    const typeDefs = [
      schema.buildObjectType({
        name: `User`,
        interfaces: [`Node`],
        fields: {
          address: `UserAddress`,
        },
      }),
      schema.buildObjectType({
        name: `UserAddress`,
        fields: {
          street: {
            type: `String!`,
          },
          state: `String`,
        },
      }),
    ]

    createTypes(typeDefs)
  }
```

