TITLE: Implementing Authentication Routes in NodeJS Server
DESCRIPTION: HTTP server implementation with routes for handling user sign-up, sign-in, email verification, and password reset functionality. Routes include request handlers for different authentication endpoints.
SOURCE: https://github.com/geldata/gel/blob/master/docs/reference/auth/email_password.rst#2025-04-16_snippet_2

LANGUAGE: javascript
CODE:
```
const server = http.createServer(async (req, res) => {
  const requestUrl = getRequestUrl(req);

  switch (requestUrl.pathname) {
    case "/auth/signup": {
      await handleSignUp(req, res);
      break;
    }

    case "/auth/signin": {
      await handleSignIn(req, res);
      break;
    }

    case "/auth/verify": {
      await handleVerify(req, res);
      break;
    }

    case "/auth/send-password-reset-email": {
      await handleSendPasswordResetEmail(req, res);
      break;
    }

    case "/auth/ui/reset-password": {
      await handleUiResetPassword(req, res);
      break;
    }

    case "/auth/reset-password": {
      await handleResetPassword(req, res);
      break;
    }

    default: {
      res.writeHead(404);
      res.end("Not found");
      break;
    }
  }
});

/**
 * Handles sign up with email and password.
 *
 * @param {Request} req
 * @param {Response} res
 */
const handleSignUp = async (req, res) => {
  let body = "";
  req.on("data", (chunk) => {
    body += chunk.toString();
  });
  req.on("end", async () => {
    const pkce = generatePKCE();
    const { email, password, provider } = JSON.parse(body);
    if (!email || !password || !provider) {
      res.status = 400;
      res.end(
        `Request body malformed. Expected JSON body with 'email', 'password', and 'provider' keys, but got: ${body}`,
      );
      return;
    }

    const registerUrl = new URL("register", GEL_AUTH_BASE_URL);
    const registerResponse = await fetch(registerUrl.href, {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        challenge: pkce.challenge,
        email,
        password,
        provider,
        verify_url: `http://localhost:${SERVER_PORT}/auth/verify`,
      }),
    });

    if (!registerResponse.ok) {
      const text = await registerResponse.text();
      res.status = 400;
      res.end(`Error from the auth server: ${text}`);
      return;
    }

    const registerJson = await registerResponse.json();

    if ("code" in registerJson) {
      // No verification required, we can immediately get an auth token
      const tokenUrl = new URL("token", GEL_AUTH_BASE_URL);
      tokenUrl.searchParams.set("code", registerJson.code);
      tokenUrl.searchParams.set("verifier", pkce.verifier);
      const tokenResponse = await fetch(tokenUrl.href, {
        method: "get",
      });

      if (!tokenResponse.ok) {
        const text = await tokenResponse.text();
        res.status = 400;
        res.end(`Error from the auth server: ${text}`);
        return;
      }

      const { auth_token } = await tokenResponse.json();
      res.writeHead(204, {
        "Set-Cookie": `gel-auth-token=${auth_token}; HttpOnly; Path=/; Secure; SameSite=Strict`,
      });

      res.end();
    } else {
      // Verification required, we need to render a notice to the user
      // to check their email for a verification link
      res.writeHead(200, { "Content-Type": "text/html" });
      res.end(`
        <html>
          <body>
            <p>Please check your email for a verification link.</p>
          </body>
        </html>
      `);
    }
  });
};

/**
 * Handles sign in with email and password.
 *
 * @param {Request} req
 * @param {Response} res
 */
const handleSignIn = async (req, res) => {
  let body = "";
  req.on("data", (chunk) => {
    body += chunk.toString();
  });
  req.on("end", async () => {
    const pkce = generatePKCE();
    const { email, password, provider } = JSON.parse(body);
    if (!email || !password || !provider) {
      res.status = 400;
      res.end(
        `Request body malformed. Expected JSON body with 'email', 'password', and 'provider' keys, but got: ${body}`,
      );
      return;
    }

    const authenticateUrl = new URL("authenticate", GEL_AUTH_BASE_URL);
    const authenticateResponse = await fetch(authenticateUrl.href, {
      method: "post",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        challenge: pkce.challenge,
        email,
        password,
        provider,
      }),
    });

    if (!authenticateResponse.ok) {
      const text = await authenticateResponse.text();
      res.status = 400;
      res.end(`Error from the auth server: ${text}`);
      return;
    }

    const authenticateJson = await authenticateResponse.json();

    if ("code" in authenticateJson) {
      // User is verified, we can get an auth token
      const tokenUrl = new URL("token", GEL_AUTH_BASE_URL);
      tokenUrl.searchParams.set("code", authenticateJson.code);
      tokenUrl.searchParams.set("verifier", pkce.verifier);
      const tokenResponse = await fetch(tokenUrl.href, {
        method: "get",
      });

      if (!tokenResponse.ok) {
        const text = await tokenResponse.text();
        res.status = 400;
        res.end(`Error from the auth server: ${text}`);
        return;
      }
```

