TITLE: Implementing Bulk Data Import with Gel in TypeScript
DESCRIPTION: Demonstrates how to import deck and card data using Gel database operations. Includes creating cards and linking them to a deck using UUID casting.
SOURCE: https://github.com/geldata/gel/blob/master/docs/intro/quickstart/working/nextjs.rst#2025-04-16_snippet_0

LANGUAGE: typescript
CODE:
```
"use server";

import { client } from "@/lib/gel";
import e from "@/dbschema/edgeql-js";
import { revalidatePath } from "next/cache";
import { RawJSONDeck } from "@/lib/models";

export async function importDeck(formData: FormData) {
  const file = formData.get("file") as File;
  const rawDeck = JSON.parse(await file.text()) as RawJSONDeck;
  const deck = {
    ...rawDeck,
    cards: rawDeck.cards.map((card, index) => ({
      ...card,
      order: index,
    })),
  };
  const cardIds: string[] = [];
  for (const card of deck.cards) {
    const createdCard = await e
      .insert(e.Card, {
        front: card.front,
        back: card.back,
        order: card.order,
      })
      .run(client);

    cardIds.push(createdCard.id);
  }

  const cardIdsLiteral = e.literal(e.array(e.uuid), cardIds);

  await e.insert(e.Deck, {
    name: deck.name,
    description: deck.description,
    cards: e.select(e.Card, (c) => ({
      filter: e.contains(cardIdsLiteral, c.id),
    })),
  }).run(client);

  revalidatePath("/");
}
```

