    required app_id: int32 {
        constraint exclusive;
    }
    title: str;

    # Let's add a computed link back to posts.
    posts := .<thread[is Post];
}

type Post {
    required app_id: int32 {
        constraint exclusive;
    }
    required body: str {
        constraint exclusive;
    }
    required creation_time: datetime {
        default := datetime_current();
