    <script src="https://unpkg.com/convex@1.0.3/dist/browser.bundle.js"></script>
  </head>
  <body>
    <h1>Messages</h1>
    <ul id="messages"></ul>
    <input id="name" type="text" placeholder="Your name" />
    <input id="body" type="text" placeholder="Your message" />
    <button onclick="handleSend()">Send</button>
    <script>
      const { ConvexClient } = window.convex;
      const client = new ConvexClient("CONVEX_URL_GOES_HERE");

      async function handleSend() {
        const name = document.getElementById("name").value;
        const body = document.getElementById("body").value;
        await client.mutation("sendMessage", { body, author: name });
        document.getElementById("body").value = "";
      }

      client
        .query("listMessages")
        .then((messages) => {
          const messagesEl = document.getElementById("messages");
          messagesEl.innerHTML = messages
            .map((message) => `<li>${message.author}: ${message.body}</li>`)
            .join("");
        })
        .catch((error) => {
          console.error("Error fetching messages:", error);
        });
    </script>
  </body>
</html>
