  return (
    <main>
      <h1>Convex + Clerk + React</h1>
      <Authenticated>
        <p>You're logged in!</p>
        <SignOutButton>
          <button>Sign out</button>
        </SignOutButton>
        <Content />
      </Authenticated>
      <Unauthenticated>
        <p>You're not logged in!</p>
