  const file = formData.get("image") as File;

  const postUrl = await generateUploadUrl();
  const result = await fetch(postUrl, {
    method: "POST",
    headers: {
      "Content-Type": file.type,
    },
    body: file,
  });
  const { storageId } = await result.json();
  await sendImage(storageId);
};
