import { mutation } from "./_generated/server";

export const createUserWithPreferences = mutation(async ({ db }) => {
  // Create a preferences document
  const preferencesId = await db.insert("preferences", {
    userId: null,
  });

  // Create a user document
  const userId = await db.insert("users", {
    preferencesId,
  });

  // Update the preferences document with the user ID
  await db.patch(preferencesId, {
    userId,
  });

  return { userId, preferencesId };
});
```

