        const { dismissOverlay, message } = params;
        const actions = messageActions({ ...params });
        if (actions) {
          actions.push({
            action: async () => {
              try {
                await chatClient.blockUser(message.user?.id || '');
                dismissOverlay();
              } catch (error) {
                console.log('Error blocking user:', error);
