TITLE: Integrating React Navigation Stack in Stream Chat Channel
DESCRIPTION: This code snippet integrates React Navigation into the Stream Chat Channel component to handle poll-related screens. It creates a ChannelStack using createStackNavigator and defines a ChannelMessageList component to encapsulate the ChannelHeader, MessageList, and MessageInput. The ChannelStack.Navigator is then embedded within the Channel component, allowing for custom navigation between the main channel view and the PollResultsScreen.
SOURCE: https://github.com/getstream/stream-chat-react-native/blob/develop/docusaurus/reactnative_versioned_docs/version-5.x.x/guides/custom-poll-flow.mdx#_snippet_2

LANGUAGE: typescript
CODE:
```
import { OverlayProvider, Chat, Channel, MessageList, MessageInput, PollContent } from 'stream-chat-react-native';
// highlight-next-line
import { createStackNavigator } from '@react-navigation/stack';

const MyPollButtons = () => {
  return (
    <>
      <ShowAllOptionsButton />
      <ViewResultsButton
        onPress={({ message, poll }) => Alert.alert(`Message ID: ${message.id} and Poll ID: ${poll.id}`)}
      />
      <EndVoteButton />
    </>
  );
};

const MyPollContent = () => <PollContent PollHeader={() => null} PollButtons={MyPollButtons} />;

// highlight-start
const ChannelMessageList = () => {
  return (
    <>
      <ChannelHeader />
      <MessageList />
      <MessageInput />
    </>
  );
};
// highlight-end

// highlight-next-line
const ChannelStack = createStackNavigator<StackNavigatorParamList>();

const ChannelScreen = () => {
  return (
    <OverlayProvider>
      <Chat client={client}>
        <Channel channel={channel} PollContent={MyPollContent}>
          // highlight-start
          <ChannelStack.Navigator initialRouteName={'ChannelMessageList'}>
            <ChannelStack.Screen
              name={'ChannelMessageList'}
              options={{ headerShown: false }}
              component={ChannelMessageList}
            />
            <ChannelStack.Screen name={'PollResultsScreen'} options={{ headerShown: false }} component={() => null} />
          </ChannelStack.Navigator>
          // highlight-end
        </Channel>
      </Chat>
    </OverlayProvider>
  );
};
