name:&#x27;TextInput&#x27;,
props:{
value:&#x27;string&#x27;,
onChange:{
type:&#x27;eventHandler&#x27;,
argTypes:[{name:&#x27;event&#x27;,type:&#x27;object&#x27;}]
}
},
states:{
value:{
type:&#x27;writable&#x27;,
valueProp:&#x27;value&#x27;,
variableType:&#x27;text&#x27;,
