name:&quot;DataTable&quot;,
props:{
data:{
type:&quot;object&quot;
},
onSelectedRowChange:{
type:&quot;eventHandler&quot;,
argTypes:[{name:&quot;row&quot;,type:&quot;object&quot;}]
}
},
states:{
selectedRow:{
// This state cannot be controlled by the parent -- so can only be read
type:&quot;readonly&quot;,
variableType:&quot;object&quot;,
