  const {...props}
  return (
    <Button
      onPress={() => {
        toast.show({
          placement: 'top',
          duration: duration,
          render: ({ id }) => {
            const toastId = "toast-" + id;
            return (
              <Toast bg='$secondary700' nativeID={toastId} p='$3'>
