              handleChange(Math.floor(v));
            }} 
            onChangeEnd={v => {
              v && setOnChangeEndValue(Math.floor(v));
            }}
            >
            <SliderTrack>
              <SliderFilledTrack />
            </SliderTrack>
            <SliderThumb />
          </Slider>
        </Center>
        <Volume2Icon/>
      </HStack>
    </VStack>
  );
}
