  const handleChange = (value) => {
    setSliderValue(value);
  };
  return (
     <HStack space="lg">
     <Text size="md">$0</Text>
        <Tooltip
              placement={"top"}
              trigger={(triggerProps) => {
                return (
                      <Center w="$80">
                          <Slider
                           step={5}
                            sliderTrackHeight={4}
                            value={sliderValue}
                            maxValue={60}
                            minValue={0}
                            onChange={v => {
                              handleChange(Math.floor(v));
                            }}>
                            <SliderTrack>
                              <SliderFilledTrack />
                            </SliderTrack>
                            <SliderThumb {...triggerProps}/>
                          </Slider>
                      </Center>
                )
              }}
            >
            <TooltipContent>
              <Text color="white">{"$" + sliderValue}</Text>
