    setIsOpen(true);
  };
  const handleClose = () => {
    setIsOpen(false);
  };
  return (
    <Popover
      isOpen={isOpen}
      onClose={handleClose}
      onOpen={handleOpen}
      {...props}
      trigger={(triggerProps) => {
        return (
          <Button
            {...triggerProps}
          >
            <ButtonText>
              Popover
            </ButtonText>
          </Button>
        );
      }}
    >
      <PopoverBackdrop/>
      <PopoverContent>
        <PopoverHeader>
          <Heading size='lg'>Welcome!</Heading>
          <PopoverCloseButton>
            <Icon as={CloseIcon}/>
